		#{item}



	AND c.channel_id in

		#{item}



	AND c.area_id in

		#{item}


)s group by channelName,channelId

select sendTime,colorLevelName,count(colorLevelName) count from (
select distinct w.id taskId,
case w.disaster_color when 0 then '红色' when 1 then '橙色' when 2 then '黄色' else '蓝色' end colorLevelName,
DATE_FORMAT(w.send_time, '%Y-%m-%d') sendTime from warn_edit w
left join warn_edit_content c on w.id=c.warn_edit_id
where 1=1 and w.status=1 and FIND_IN_SET(w.area_id, getAreaCList(#{empAreaId}))

			AND DATE_FORMAT(w.send_time, '%Y-%m-%d %H:%i:%s') >= #{startTime}


			AND DATE_FORMAT(w.send_time, '%Y-%m-%d %H:%i:%s') <= #{endTime}


	AND w.disaster_id IN

		#{item}



	AND w.disaster_color IN

		#{item}



	AND c.channel_id in

		#{item}



	AND c.area_id in

		#{item}


) a group by sendTime,colorLevelName order by sendTime desc

select distinct w.id taskId, w.title, w.disaster_id disasterId, w.disaster_name disasterName,w.disaster_color, case w.disaster_color when 0 then '红色' when 1 then '橙色' when 2 then '黄色' else '蓝色' end colorLevelName, DATE_FORMAT(w.send_time, '%Y-%m-%d %H:%i:%s') sendTime, d.icon, a.id areaId,a.area_name areaName, a.longitude,a.latitude,c.content,o.organization_name orgName from warn_edit w LEFT JOIN organization o on w.organization_id=o.id LEFT JOIN warn_edit_content c on w.id=c.warn_edit_id LEFT JOIN area a on c.area_id=a.id LEFT JOIN disaster d on w.disaster_id=d.id LEFT JOIN channel ch on ch.id=c.channel_id where 1=1 and w.status=1 and a.level=3
AND DATE_FORMAT(w.send_time, '%Y-%m-%d %H:%i:%s') >= #{startTime}
AND DATE_FORMAT(w.send_time, '%Y-%m-%d %H:%i:%s') <= #{endTime}
AND w.disaster_id IN
#{item}
AND w.disaster_color IN
#{item}
AND c.channel_id in
#{item}
AND c.area_id in
#{item}
ORDER BY w.send_time DESC

select ch.name ,c.content from  warn_edit_content c
left join channel ch  on c.channel_id=ch.id
where c.warn_edit_id=#{taskId} and c.area_id=#{areaId}
